namespace Authentication.Models.Rest.Response
{

    public record LoginResponse
    {

        /// <summary>
        /// The JWT login token to use as a bearer authorization header for other API requests.
        /// </summary>
        /// <example name="success">eyJhbGciOiJodHRwOi8vd3d3LnczLm9yZy8yMDAxLzA0L3htbGRzaWctbW9yZSNobWFjLXNoYTI1NiIsInR5cCI6IkpXVCJ9.._vs4TzB4FEzTDOdXNRVfqHafIgb_e3JRGyY1db7W1hw</example>
        public string AccessToken { get; init; }

        /// <summary>
        /// The JWT token used to generate a new access token.
        /// </summary>
        /// <example>eyJhbGciOiJodHRwOi8vd3d3LnczLm9yZy8yMDAxLzA0L3htbGRzaWctbW9yZSNobWFjLXNoYTUxMiIsInR5cCI6IkpXVCJ9..nfGeHZ7DolmAnovcp70miWP6h3kL5OYZM6ZMIOejOKePrsiUNZSokaJu49YV-rmR_mbXGG_xZZQA8AQWUQjrjw</example>
        public string RefreshToken { get; init; }

    }

}
